    const fields = lines[index];
    let fieldsA = fields.split(' ')
    for (let j = 0; j < fieldsA.length; j++) {
        if(fieldsA[j] === "" && fieldsA[j+1] === ""){
            fieldsA.splice(j,1)
            j--
        };
        if(fieldsA[j] !== "" && fieldsA[j + 1] !== ""){
            fieldsA[j] = fieldsA[j] + ' ' + fieldsA[j+1]
            fieldsA.splice(j+1, 1)
        }
    }
    let connection = {}
    let cleanConnection = fieldsA.filter(field => { return field !== "" })
    if (cleanConnection[0] === "*"){
        connection.icon_name = "fa-solid fa-wifi"
        cleanConnection.splice(0,1)
    }
    connection.BSSID = cleanConnection[0]
    connection.SSID = cleanConnection[1]
    connection.mode = cleanConnection[2]
    connection.chan = cleanConnection[3]
    connection.rate = cleanConnection[4] + ' ' + cleanConnection[5]
    connection.signal = cleanConnection[6]
    connection.bars = cleanConnection[7]
    connection.security = [cleanConnection[8],cleanConnection[9]]
    //For the lsit node
    connection.title = connection.SSID
    connection.description = connection.signal
    connection.menu = ["Connect"]
    connections.push(connection)

}
msg.connexions = connections
msg.payload = connections
return msg;

